select CL.ENTITY_ID , CL.CARRIER_ID , CARRIER_STATE , CARRIER_STATE_CODE , PREV_CARRIER_STATE , CARRIER_TYPE_ID , CARRIER_SHAPE_ID , CURRENT_AMHS_ID , CURRENT_LOC , HOST_ZONE_ID , SHELF_NO , PREV_AMHS_ID , PREV_CARRIER_LOC , STORED_TIME , CARRIER_EMPTY , WAIT_IN_TIME , WAIT_OUT_TIME , INSYSTEM_CARRIER_ID , INSYSTEM_TIME , CARRIER_ALARM , CARRIER_ACTION_CTRL , CARRIER_ACTION_TIME , PASSING_MODE , AVAILABLE , SYSTEM_STATE , ALARM_STATE , COMM_STATE , CONTROL_STATE , AMHS_L_STATE , CARRIER_JOB_ID , ALIAS , CLEAN_REQUEST , CONTAMI_ID , MES_MESSAGE , SET_TIME , PRODUCT_ID , LOT_ID from ( select
from ( select ROW_NUMBER() over(order by CARRIER_ID asc) rn , ENTITY_ID , CARRIER_ID , (select STRING from STRING_MASTER where KEY = 'CARRIER_STATE_STRING' and CODE = CARRIER.CARRIER_STATE) AS CARRIER_STATE , CARRIER_STATE AS CARRIER_STATE_CODE , (select STRING from STRING_MASTER where KEY = 'CARRIER_STATE_STRING' and CODE = CARRIER.PREV_CARRIER_STATE) AS PREV_CARRIER_STATE , CARRIER_TYPE_ID , CARRIER_SHAPE_ID , CURRENT_AMHS_ID , CURRENT_LOC , HOST_ZONE_ID , SHELF_NO , PREV_AMHS_ID , PREV_CARRIER_LOC , STORED_TIME , (select STRING from STRING_MASTER where KEY = 'CARRIER_EMPTY_STRING' and CODE = CARRIER.CARRIER_EMPTY) AS CARRIER_EMPTY , WAIT_IN_TIME , WAIT_OUT_TIME , INSYSTEM_CARRIER_ID , INSYSTEM_TIME , (select STRING from STRING_MASTER where KEY = 'CARRIER_ALARM_STRING' and CODE = CARRIER.CARRIER_ALARM) AS CARRIER_ALARM , (select STRING from STRING_MASTER where KEY = 'CARRIER_ACTION_CTRL_STRING' and CODE = CARRIER.CARRIER_ACTION_CTRL) AS CARRIER_ACTION_CTRL , CARRIER_ACTION_TIME , (SELECT STRING FROM STRING_MASTER WHERE KEY = 'PASSING_MODE_STRING' AND CODE = CARRIER.PASSING_MODE) AS PASSING_MODE , (SELECT TRIM(SUBSTR(ALIAS, INSTR(ALIAS, ' '))) as ALIAS from HOST_ALIAS where KEY = CARRIER.CURRENT_LOC AND CATEGORY = 'HOST') AS ALIAS , CLEAN_REQUEST , CONTAMI_ID from CARRIER
)
) CL left join AMHS_STATE on CURRENT_AMHS_ID = AMHS_STATE.AMHS_ID left join CARRIER_JOB on CL.ENTITY_ID = CARRIER_JOB.ENTITY_ID left join CARRIER_ATTRIBUTE_INFO on CL.CARRIER_ID = CARRIER_ATTRIBUTE_INFO.CARRIER_ID order by CARRIER_ID asc

select
from ( select row_number() over(order by CARRIER_ID ASC) rn,
from CARRIER
)
