mapper.select:

select 

from STOCKER_ZONE
where LLC_ID = #{llcId,jdbcType=VARCHAR}
  and TSC_ID = #{tscId,jdbcType=VARCHAR}
  and ZONE_ID = #{zoneId,jdbcType=VARCHAR}

mapper.delete:

delete from STOCKER_ZONE
where LLC_ID = #{llcId,jdbcType=VARCHAR}
  and TSC_ID = #{tscId,jdbcType=VARCHAR}
  and ZONE_ID = #{zoneId,jdbcType=VARCHAR}
